import Data.List

signal :: String
signal = ""

slidesOfLength :: Int -> [String]
slidesOfLength x = foldr (zipWith (:)) (repeat []) $ take x $ iterate (drop 1) signal

indexFirstIntraUnique :: [String] -> Int
indexFirstIntraUnique xs = snd $ head $ filter (\(x, _) -> x == nub x) $ zip xs [1..]

problem1Solution = (+3) $ indexFirstIntraUnique (slidesOfLength 4)
problem2Solution = (+13) $ indexFirstIntraUnique (slidesOfLength 14)

main :: IO ()
main = do { putStrLn (show problem1Solution)
          ; putStrLn (show problem2Solution) }
